    private static int somme(int a, int b) {
        calculatrice.NewWebService_Service service = new calculatrice.NewWebService_Service();
        calculatrice.NewWebService port = service.getNewWebServicePort();
        return port.somme(a, b);
    }

    private static int facto(int n) {
        calculatrice.Facto_Service service = new calculatrice.Facto_Service();
        calculatrice.Facto port = service.getFactoPort();
        return port.facto(n);
    }

    private static int soustraction(int a, int b) {
        soustraction.Soustraction_Service service = new soustraction.Soustraction_Service();
        soustraction.Soustraction port = service.getSoustractionPort();
        return port.soustraction(a, b);
    }

    private static int divsion(int arg0, int arg1) {
        division.Division_Service service = new division.Division_Service();
        division.Division port = service.getDivisionPort();
        return port.divsion(arg0, arg1);
    }
   
